// Code added here will be run once
// whenever the node is started.
global.set('fault_counter',0)

func:
let timer_1 = flow.get('p23_timer_1');
let timer_2 = flow.get('p23_timer_2');

let idle_condition; 


if (timer_1 || timer_2)
{
    idle_condition = true;
}
else idle_condition = false;

flow.set('P23_idle_condition', idle_condition)


msg.payload = idle_condition;

return msg;

let last_value = flow.get('last_value_p21');

let change_value = msg.payload.Press_Angle - last_value;

if (change_value == 0)
{
    flow.set('P21_idle_condition', true)
}
else flow.set('P21_idle_condition', false);

return msg;

let str = msg.payload ;

str = str.substring(0, str.indexOf(',')); //cut down the incoming string to only be until the first comma
msg.payload = str + "}"; // add the bracket after to follow expected JSON format
msg.payload = JSON.parse(msg.payload); //convert modified string into json object

let last_value = flow.get('last_value_p24'); 
let change_value = msg.payload.Press_Angle - last_value;

if (change_value == 0) {
    flow.set('P24_idle_condition', true)
}
else flow.set('P24_idle_condition', false);

return msg;

let p21_idle_conditon = flow.get('P21_idle_condition');
let p23_idle_conditon = flow.get('P23_idle_condition');
let p24_idle_conditon = flow.get('P24_idle_condition');
let manual_start_PB = flow.get('start_PB');

if (p21_idle_conditon && p23_idle_conditon && p24_idle_conditon)
{
    msg.payload = "START";
}
else 
{
    msg.payload = "STOP";
}

if (manual_start_PB)
{
    msg.payload = "STOP";
    flow.set('start_PB', false);
}

return msg;

